// UserForm.jsx
import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";

const UserForm = ({ mode }) => {
  const [user, setUser] = useState({
    id: "",
    firstName: "",
    lastName: "",
    email: "",
    department: "",
  });
  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    if (mode === "edit") {
      axios.get(`https://jsonplaceholder.typicode.com/users/${id}`).then((response) => {
        const userData = response.data;
        setUser({
          id: userData.id,
          firstName: userData.name.split(" ")[0],
          lastName: userData.name.split(" ")[1],
          email: userData.email,
          department: userData.department || "",
        });
      });
    }
  }, [id, mode]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser({ ...user, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (mode === "add") {
      axios.post("https://jsonplaceholder.typicode.com/users", user).then(() => {
        alert("User added successfully!");
        navigate("/");
      });
    } else if (mode === "edit") {
      axios.put(`https://jsonplaceholder.typicode.com/users/${id}`, user).then(() => {
        alert("User updated successfully!");
        navigate("/");
      });
    }
  };

  return (
    <div className="container">
      <h1>{mode === "add" ? "Add New User" : "Edit User"}</h1>
      <form onSubmit={handleSubmit}>
        <label>
          ID:
          <input
            type="text"
            name="id"
            value={user.id}
            onChange={handleChange}
            required
            disabled={mode === "edit"} // Disable ID input in edit mode
          />
        </label>
        <label>
          First Name:
          <input
            type="text"
            name="firstName"
            value={user.firstName}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Last Name:
          <input
            type="text"
            name="lastName"
            value={user.lastName}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={user.email}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Department:
          <input
            type="text"
            name="department"
            value={user.department}
            onChange={handleChange}
            required
          />
        </label>
        <button type="submit">Save</button>
      </form>
      <button onClick={() => navigate("/")}>Cancel</button>
    </div>
  );
};

export default UserForm;
