import React, { useState, useEffect } from "react";
import axios from "axios";
import UserForm from "./UserForm";
import './UserList.css';  // Import the external CSS file

const UserList = () => {
    const [users, setUsers] = useState([]);
    const [error, setError] = useState("");
    const [showForm, setShowForm] = useState(false);
    const [editingUser, setEditingUser] = useState(null);

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const response = await axios.get("https://jsonplaceholder.typicode.com/users");
                setUsers(response.data);
            } catch (err) {
                setError("Failed to fetch users. Please try again later.");
            }
        };
        fetchUsers();
    }, []);

    const handleDelete = async (id) => {
        try {
            await axios.delete(`https://jsonplaceholder.typicode.com/users/${id}`);
            setUsers(users.filter((user) => user.id !== id));
        } catch {
            setError("Failed to delete the user. Please try again.");
        }
    };

    const handleEdit = (user) => {
        setEditingUser(user);
        setShowForm(true);
    };

    const handleAdd = () => {
        setEditingUser(null);
        setShowForm(true);
    };

    const handleSave = (newUser) => {
        if (editingUser) {
            setUsers(users.map((user) => (user.id === newUser.id ? newUser : user)));
        } else {
            setUsers([...users, { ...newUser, id: Date.now() }]);
        }
        setShowForm(false);
    };

    return (
        <div className="user-list-container">
            <h2 style={{
                textAlign: 'left',
                margin: '20px',
                fontSize: '1rem',
                '@media (maxWidth: 768px)': {
                    fontSize: '.8 rem',
                    margin: '15px'
                },
                '@media (max-width: 480px)': {
                    fontSize: '.6 rem',
                    margin: '10px'
                }
            }}>User List</h2>

            <table className="user-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>First Name</th>
                        <th>Last Name</th>
                        <th>Email</th>
                        <th>Department</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user) => (
                        <tr key={user.id}>
                            <td>{user.id}</td>
                            <td>{user.name.split(" ")[0]}</td>
                            <td>{user.name.split(" ")[1]}</td>
                            <td>{user.email}</td>
                            <td>{user.department || "N/A"}</td>
                            <td>
                                <button onClick={() => handleEdit(user)} className="edit-btn">Edit</button>
                                <button onClick={() => handleDelete(user.id)} className="delete-btn">Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            {showForm && (
                <UserForm
                    user={editingUser}
                    onSave={handleSave}
                    onCancel={() => setShowForm(false)}
                />
            )}
            <button onClick={handleAdd} className="add-btn">Add User</button>
            {error && <p className="error-message">{error}</p>}
        </div>
    );
};

export default UserList;
