import React, { useState, useEffect } from "react";
import axios from "axios";
import UserForm from "./UserForm";

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState("");
  const [showForm, setShowForm] = useState(false);
  const [editingUser, setEditingUser] = useState(null);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.get("https://jsonplaceholder.typicode.com/users");
        setUsers(response.data);
      } catch (err) {
        setError("Failed to fetch users. Please try again later.");
      }
    };
    fetchUsers();
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`https://jsonplaceholder.typicode.com/users/${id}`);
      setUsers(users.filter((user) => user.id !== id));
    } catch {
      setError("Failed to delete the user. Please try again.");
    }
  };

  const handleEdit = (user) => {
    setEditingUser(user);
    setShowForm(true);
  };

  const handleAdd = () => {
    setEditingUser(null);
    setShowForm(true);
  };

  const handleSave = (newUser) => {
    if (editingUser) {
      setUsers(users.map((user) => (user.id === newUser.id ? newUser : user)));
    } else {
      setUsers([...users, { ...newUser, id: Date.now() }]);
    }
    setShowForm(false);
  };

  // Styles
  const tableStyle = {
    width: "100%",
    borderCollapse: "collapse",
    marginBottom: "20px",
    overflowX: "auto",
  };

  const thStyle = {
    backgroundColor: "#4CAF50",
    color: "white",
    padding: "10px",
    textAlign: "left",
  };

  const tdStyle = {
    border: "1px solid #ddd",
    padding: "8px",
    textAlign: "left",
  };

  const buttonStyle = {
    padding: "8px 12px",
    borderRadius: "4px",
    border: "none",
    cursor: "pointer",
    fontSize: "14px",
    marginRight: "5px",
  };

  const editButtonStyle = {
    ...buttonStyle,
    backgroundColor: "#4CAF50",
    color: "white",
  };

  const deleteButtonStyle = {
    ...buttonStyle,
    backgroundColor: "#f44336",
    color: "white",
  };

  const addButtonStyle = {
    padding: "10px 20px",
    backgroundColor: "#008CBA",
    color: "white",
    borderRadius: "4px",
    border: "none",
    cursor: "pointer",
    fontSize: "16px",
    marginBottom: "20px",
    display: "block",
    marginLeft: "auto",
    marginRight: "auto",
  };

  const errorStyle = {
    color: "red",
    fontSize: "16px",
    textAlign: "center",
    marginTop: "20px",
  };

  const headerContainerStyle = {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    flexDirection: "column",
    textAlign: "center",
    marginBottom: "20px",
  };

  const userManagementHeaderStyle = {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    width: "100%",
    backgroundColor: "#333",
    color: "white",
    padding: "20px 0",
    fontSize: "24px",
    fontWeight: "bold",
  };

  return (
    <div style={{ padding: "20px" }}>
      {/* User Management Header */}
      <div style={userManagementHeaderStyle}>
        <h1>User Management Application</h1>
      </div>

      {/* User List Header and Add Button */}
      <div style={headerContainerStyle}>
        <h3>User List</h3>
        <button className="btn-add" onClick={handleAdd} style={addButtonStyle}>
          Add User
        </button>
      </div>

      {/* Table displaying user list */}
      <table style={tableStyle}>
        <thead>
          <tr>
            <th style={thStyle}>ID</th>
            <th style={thStyle}>First Name</th>
            <th style={thStyle}>Last Name</th>
            <th style={thStyle}>Email</th>
            <th style={thStyle}>Department</th>
            <th style={thStyle}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td style={tdStyle}>{user.id}</td>
              <td style={tdStyle}>{user.name.split(" ")[0]}</td>
              <td style={tdStyle}>{user.name.split(" ")[1]}</td>
              <td style={tdStyle}>{user.email}</td>
              <td style={tdStyle}>{user.department || "N/A"}</td>
              <td style={tdStyle}>
                <button onClick={() => handleEdit(user)} style={editButtonStyle}>
                  Edit
                </button>
                <button onClick={() => handleDelete(user.id)} style={deleteButtonStyle}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Conditionally render UserForm */}
      {showForm && (
        <UserForm
          user={editingUser}
          onSave={handleSave}
          onCancel={() => setShowForm(false)}
        />
      )}

      {/* Add User button placed below the table */}
      <div style={{ textAlign: "center" }}>
        <button onClick={handleAdd} style={addButtonStyle}>
          Add User
        </button>
      </div>

      {/* Error message */}
      {error && <p style={errorStyle}>{error}</p>}
    </div>
  );
};

export default UserList;
